function randomString(num) {
	var uniqueText = "";
	var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
	
	for (var i = 0; i < num; i++)
		uniqueText += possible.charAt(Math.floor(Math.random() * possible.length));
	
	return uniqueText;
}


module.exports = randomString;
